    def write_percent(self, initial_deposit, target_amount, percent):
        deposit = BankDeposit(initial_deposit, percent)
        if target_amount <= initial_deposit:
            self.textBrowser.setText(f"‚ö†Ô∏è–û—à–∏–±–∫–∞\n–ù–∞—á–∞–ª—å–Ω–∞—è —Å—É–º–º–∞ –ø—Ä–µ–≤—ã—à–∞–µ—Ç –∫–æ–Ω–µ—á–Ω—É—é!")
        else:
            months, final_deposit = deposit.calculate_time_to_reach_target(target_amount)
            self.textBrowser.setText(
                f"–ß–µ—Ä–µ–∑ {months} –º–µ—Å—è—Ü–µ–≤ —Ä–∞–∑–º–µ—Ä –≤–∫–ª–∞–¥–∞ –ø—Ä–µ–≤—ã—Å–∏—Ç - {target_amount} —Ä—É–±–ª–µ–π.\n–ò—Ç–æ–≥–æ–≤—ã–π —Ä–∞–∑–º–µ—Ä –≤–∫–ª–∞–¥–∞ —Å–æ—Å—Ç–∞–≤–∏—Ç - {final_deposit:.2f} —Ä—É–±–ª–µ–π.üí∞")


